mapper.insert:
insert into patient(name, age, gender, status, diagnosis, last_update) VALUES (
#{name}, #{age}, #{gender}, #{status}, #{diagnosis}, #{lastUpdate}
)

mapper.select:
select * from patient

    and name = #{name}
    and age = #{age}
    and gender = #{gender}
    and status = #{status}
    and diagnosis = #{diagnosis}
    and last_update = #{lastUpdate}
